#!/bin/bash

# The purpose of this script is to silence the warning when you
# connect to a new host via SSH.... without just disabling the check
# (that would be wildly insecure).

# ssh-keyscan github.com
GITHUB_HOST_KEY='github.com ssh-rsa '

mkdir -p "$HOME/.ssh"
chmod og-rwx "$HOME/.ssh"
echo "$GITHUB_HOST_KEY" >> "$HOME/.ssh/known_hosts"
